mapper.select:
select o.product_id, o.user_phone, o.status, o.create_time, p.product_id as "product.product_id", p.name as "product.name", p.stock as "product.stock", p.start_time as "product.start_time", p.end_time as "product.end_time", p.create_time as "product.create_time"
from order_ o inner join product p on o.product_id = p.product_id
where o.product_id = #{productId} and o.user_phone = #{userPhone};
